mapper.insert:
INSERT INTO tn_board (
b_title,
b_content,
b_writer
) VALUES (
#{bTitle}, 
#{bContent}, 
#{bWriter} 
)

mapper.update:
UPDATE		tn_board 
SET			b_title = #{bTitle},
			b_content = #{bContent} 
WHERE		b_id = #{bId}

mapper.delete:
DELETE		tn_board.* 
FROM		tn_board 
WHERE		b_id = #{bId}

mapper.update:
		 	UPDATE		tn_board 
 			SET			b_order = b_order + 1
 			WHERE		b_order >= #{bOrder}
 						AND b_id != (SELECT LAST_INSERT_ID()) 
 						AND b_group = #{bGroup}
